import React, {useState} from 'react';
import {IoIosArrowForward, IoIosArrowBack} from 'react-icons/io';
import './Mobile_slider_style.css';

export const MobileSlider = ({slides}) => {
  const [current, setCurrent] = useState(0);
  const [slideData, setSlideData] = useState(slides);
  const length = slides.length;

  const nextSlide = () =>{
    setCurrent(current === length - 1 ? 0 : current + 1);
  }

  const prevSlide = () => {
    setCurrent(current === 0 ? length - 1 : current - 1);
  }

  // Check if the props is an array or have contents inside
  if(!Array.isArray(slides) || length <= 0){
    return null;
  }

  return (
  <div className="mobile-slider-container">
    <IoIosArrowForward />
    <IoIosArrowBack />
      {slides.map( (slide, index) => {
        return <div className="mobile-slide-container">
                  <img src ={slide.img} alt={slide.company} />
               </div>;
      })}
  </div>
  )
}
